import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const CartTotal = () => {
  const { cart } = useSelector((cart) => cart);
  const [totalAmount, setTotalAmount] = useState(0);
  useEffect(() => {
    setTotalAmount(cart.reduce((acc,curr)=>acc))
  }, [cart]);

  return (
    <div className=" bg-red-200 w-[30%] flex flex-col  items-center">
      <div className="flex flex-col w-[100%] h-[100%] bg-red-500 items-center ">
        <h1>Cart Total</h1>
        <div className="top">
          <h1>Total Price: {totalAmount} </h1>
        </div>
        <div className="btm flex  justify-center items-center ">
          <button className="btm bg-green-600 text-black  rounded-md  py-2 px-6">
            Checkout
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartTotal;
