import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { add,remove  } from "../redux/Slices/cartSlice";
import { toast } from "react-hot-toast";

const Product = ({ item }) => {
  const { cart } = useSelector((cart) => cart);
  const dispatch = useDispatch();
  

  const  addToCart=()=> {
    dispatch(add(item));
    toast.success('Item Add to Cart');
  }
  const  removeFromCart=()=> {
    dispatch(remove(item.id));
    toast.error("Item Removed from cart")
  }

  return (
    <div className="bg-white border-4 hover:scale-110 transition-all duration-300 ease-in  w-full md:w-[50%] lg:w-[30%]  p-4 text-black flex flex-col items-center rounded-md justify-between">
      <p className="text-red-800"> {item.title} </p>
      <p>
        
        {item.description.length < 100
          ? item.description
          : item.description.substring(0, 70) + "..."}
      </p>
      <img
        className="w-[40%] h-[40%] bg-cover flex  item-center"
        src={item.image}
        alt="img"
      />

      <div className="flex flex-start">
        <div>{`${item.price} $`}</div>
        <div>Available</div>
      </div>
      <div className=" flex  gap-5 ">
        {cart.some((p) => p.id === item.id) ? (
          <button onClick={()=>{removeFromCart(item.id)}} className="button text-white bg-blue-600 rounded-md p-3">
            Remove From Cart
          </button>
        ) : (
          <button onClick={()=>{addToCart(item)}} className="button text-white bg-blue-600 rounded-md p-3">
            Add to Cart
          </button>
        )}
      </div>
    </div>
  );
};

export default Product;
