import React from "react";
import { useSelector } from "react-redux";

const Product = ({ data }) => {
   
  const cart=useSelector



  return (
    <div className="bg-white border-4 hover:scale-110 transition-all duration-300 ease-in  w-full md:w-[50%] lg:w-[30%]  p-4 text-black flex flex-col items-center rounded-md justify-between">
      <p className="text-red-800"> {data.title} </p>
      <p>
        {" "}
        {data.description.length < 100
          ? data.description
          : data.description.substring(0, 70) + "..."}
      </p>
      <img
        className="w-[40%] h-[40%] bg-cover flex  item-center"
        src={data.image}
        alt="img"
      />

      <div className="flex flex-start">
        <div>{`${data.price} $`}</div>
        <div>Available</div>
      </div>
      <div className=" flex  gap-5 ">
        <button  className="button bg-red-200 rounded-md p-3">
          Add to Cart
        </button>
        <button className="button bg-red-200 rounded-md p-3">
          Remove From Cart
        </button>
      </div>
    </div>
  );
};

export default Product;
