import React from "react";

const Product = ({ data }) => {
  console.log(data);
  return (
    <div className="bg-white p-4 text-black w-[30%] flex flex-col items-center rounded-md justify-evenly">
      <p className=""> {data.title} </p>
      <p>
        {" "}
        {data.description.length < 100
          ? data.description
          : data.description.substring(0, 100) + "..."}
      </p>
      <img
        className="w-[80%] bg-cover flex  item-center"
        src={data.image}
        alt="img"
      />

      <div className="flex space-x-5">
        <div>{`${data.price} $`}</div>
        <div>Available</div>
      </div>
      <div className=" flex items-end gap-5 ">
        <button className="button bg-red-200 rounded-md p-3"> Add to Cart</button>
        <button className="button bg-red-200 rounded-md p-3"> Remove From Cart</button>
      </div>
    </div>
  );
};

export default Product;
