import React from "react";
import { ImBin } from "react-icons/im";

const CartItemD = ({ item }) => {
  const { title, price, description, image } = item;

  return (
    <div>
      <div className=" border-2 rounded-md gap-2 bg-yellow-50 border-black p-3 flex ">
        <div className=" w-[20%] ">
          <img src={image} alt="Cart image" />
        </div>
        <div className=" flex flex-col justify-around gap-2 ">
          <div className=" flex flex-col  ">
            <h1 className=" text-red-600 ">
              {title.length > 40 ? title.slice(0, 50) + "..." : title}{" "}
            </h1>
            <p>
              {description.length > 60
                ? description.slice(0, 80) + "..."
                : description}
            </p>
          </div>
          <div className=" flex justify-around  ">
                      <h1 className=" text-blue-600 "> Price {price} $ </h1>
                      <ImBin className="bg-red-300 p6"/>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItemD;
